-- ============================================= -- Author: Алексей Колосенок -- Create date: 17.12.2019 -- Description: Удаление заказ-наряда ФЦ -- ============================================= CREATE PROCEDURE [dbo].[DeleteWorkOrderMC]
@workOrderId int, @countRowsDeleted int output
AS

BEGIN

	DECLARE 
		@age int,
		@thisYearBirthday datetime -- день рождения в текущем году

	SET @age = DATEDIFF(YEAR, @birthday, GETDATE());
	SET @thisYearBirthday = DATEADD(YEAR, @age, @birthday);

	IF @thisYearBirthday > GETDATE() -- если днюхи ещё не было, тогда...
		SET @age = @age - 1

	RETURN @age

END

